from PIL import Image, ImageFilter

image = Image.open("data:image/jpeg;base64,")

resized = image.resize((200, 200))
resized.save("output_resized.jpg")


gray = resized.convert("L")
gray.save("output_gray.jpg")

blurred = gray.filter(ImageFilter.BLUR)
blurred.save("output_blurred.jpg")

print("âœ… Image processing complete! Files saved.")
